sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var log = new GlideRecord("x_141516_radar_task_log");

	//log.get(log_sys_id);
	log.initialize();
	log.u_tasklog_parent = current.sys_id;

	if(current.isNewRecord()) {
		log.u_tasklog_action = 'Insert';
		log.u_tasklog_change = 'New Recored Created';

	} else {
		log.u_tasklog_action = 'Update';

		var change = '';

		if(previous.state != current.state)
			change = change + 'State changed from ' + previous.state + ' to ' + current.state;
		if(previous.priority != current.priority)
			change = change + '\n Priority changed from ' + previous.priority + ' to ' + current.priority;
		if(previous.assigned_to != current.assigned_to) {
			gs.info(previous.assigned_to.toString());
			change = change + '\n Assignee changed from ' + previous.assigned_to.u_radar_user.name + ' to ' +  current.assigned_to.u_radar_user.name;
		}
		log.u_tasklog_change = change;
	}

	log.insert();	
})(current, previous);
