sys_transform_script.script:
(function runTransformScript(source, map, log, target /*undefined onStart*/ ) {

	// Add your code here
	var _module =  new GlideRecord('x_141516_radar_user_role');
	_module.query();


	while(source.hasNext()) {
		source.next();

		log.info('***************** assigned to' + source.u_assigned_to);

		while(_module.hasNext()) {

			_module.next();

			if(_module.u_radar_user.user_name == source.u_assigned_to) {
				log.info('***************** 5 Matched');
				target.assigned_to = _module;
				break;
			}
		}
		log.info("--------------Target assigned to --------" + target.assigned_to);
	}



})(source, map, log, target);
